<?php/** * Events Widget Template * * Produces output for appropriate widget class in framework. * $this, $instance (sanitized field values) and $args are available. * * $this->ctfw_get_posts() can be used to produce a query according to widget field values. */// No direct accessif ( ! defined( 'ABSPATH' ) ) exit;// HTML Beforeecho $args['before_widget'];// Title$title = apply_filters( 'widget_title', $instance['title'] );if ( ! empty( $title ) ) {	echo $args['before_title'] . $title . $args['after_title'];}// Get posts$posts = ctfw_get_events( $instance ); // get events based on options - upcoming/past, limit, etc.// Loop posts$i = 0;foreach ( $posts as $post ) : setup_postdata( $post ); $i++;	// Get event meta data	// $date (localized range), $start_date, $end_date, $time, $venue, $address, $show_directions_link, $directions_url, $map_lat, $map_lng, $map_type, $map_zoom	extract( ctfw_event_data() );?><article <?php post_class( 'uplifted-widget-entry uplifted-event-widget-entry uplifted-clearfix' . ( 1 == $i ? ' uplifted-widget-entry-first' : '' ) ); ?>>	<header class="uplifted-clearfix">		<?php if ( $instance['show_image'] && has_post_thumbnail() ) : ?>			<div class="uplifted-widget-entry-thumb">				<a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>"><?php the_post_thumbnail( 'uplifted-thumb-small', array( 'class' => 'uplifted-image' ) ); ?></a>			</div>		<?php endif; ?>		<h1 class="uplifted-widget-entry-title"><a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>"><?php the_title(); ?></a></h1>		<ul class="uplifted-widget-entry-meta uplifted-clearfix">			<?php if ( $instance['show_date'] && $date ) : ?>				<li class="uplifted-widget-entry-date uplifted-events-widget-entry-date">					<?php echo esc_html( $date ); ?>				</li>			<?php endif; ?>			<?php if ( $instance['show_time'] && $time ) : ?>				<li class="uplifted-events-widget-entry-time">					<?php echo esc_html( $time ); ?>				</li>			<?php endif; ?>		</ul>	</header>	<?php if ( get_the_excerpt() && ! empty( $instance['show_excerpt'] )): ?>		<div class="uplifted-widget-entry-content">			<?php the_excerpt(); ?>		</div>	<?php endif; ?></article><?php// End Loopendforeach;// No items foundif ( empty( $posts ) ) {	?>	<div>		<?php _ex( 'There are no events to show.', 'events widget', 'uplifted' ); ?>	</div>	<?php}// HTML Afterecho $args['after_widget'];